import heapq

def contig(p, A):
    ans = []
    n = len(A)
    for i in range(n):
        for j in range(i, n):
            if i != j:
                ans.append(p[i])
            else:
                ans.append(p[j] - p[i])
    return ans

def find_top_k_subarray_sums(N, K, A):
    prefix = [A[0]]
    for x in A[1:]:
        prefix.append(prefix[-1] + x)
    return heapq.nlargest(K, contig(prefix, A))

