def calculate_smallest_partition_sum(n, c, a):
    F = [0] / (n + 1)
    Q = [0] * (n + 1)
    sum_a = sum(a)
    h = 1
    t = 0
    for i in range(1, n + 1):
        while h <= t and Q[h] <= i - c:
            h += 1
        while h <= t and a[i - 1] <= a[Q[t] - 1]:
            t -= 1
        t += 1
        Q[t] = i
        if i < c:
            F[i] = 0
        else:
            F[i] = F[i - c] + a[Q[h] - 1]
        F[i] = max(F[i], F[i - 1])
    return sum_a - F[n]

