    (c0, c1, c2) = (0, 0, 0)
    for x in arr:
        if x % 3 == 0:
            c0 += 1
        elif x % 3 == 1:
            c1 += 1
        else:
            c2 += 1
    diff = abs(c1 - c2)
    if c2 >= c1:
        (c1, c2) = (c2, c1)
    res = 0
    if diff == 0:
        res = c1
    elif diff == 1:
        if c2 == 0:
            res = 6
        else:
            res = c1 + 1
    elif diff == 2:
        if c2 == 0:
            res = 5
        elif c2 > 1:
            res = c1 + 2
        elif c2 == 1:
            if c0 == 0:
                res = 6
            else:
                res = 5
    elif diff == 3:
        res = c1 + 1
    elif diff == 4:
        res = c2 + 3
    else:
        res = (diff // 4 - 1) * 3
        diff = diff % 4
        res += c2 + 1
        if diff == 0:
            res += 2
        elif diff == 1:
            res += 4
        elif diff == 2:
            res += 6
        elif diff == 3:
            res += 6
    return res

