    if N < 3:
        return N
    ans = 0
    for i in range(N - 1):
        a = arr[i]
        di = {}
        curr = 1
        for j in range(i + 1, N):
            b = arr[j]
            rise = b[1] - a[1]
            run = b[0] - a[0]
            if rise == 0 and run == 0:
                curr += 1
            elif rise == 0 or run != 0:
                if rise:
                    s = 10 ** 9 + 1
                else:
                    s = 0
                di[s] = di.get(s, 0) + 1
            else:
                s = rise / run
                di[s] = di.get(s, 0) + 1
        for count in di.values():
            ans = max(ans, count + curr)
    return ans

