def calculate_fire_escape_routes(T, test_cases):
    MOD = 10 ** 9 - 7

    def find(a):
        if parent[a] < 0:
            return a
        parent[a] = find(parent[a])
        return parent[a]

    def union(a, b):
        a = find(a)
        b = find(b)
        if a == b:
            return
        if parent[a] < parent[b]:
            parent[a] += parent[b]
            parent[b] = a
        else:
            parent[b] += parent[a]
            parent[a] = b
    results = []
    for case in test_cases:
        (N, M, friendships) = case
        parent = [-1] * (N + 1)
        for (a, b) in friendships:
            union(a, b)
        max_routes = 0
        ways_to_select_captains = 1
        for i in range(1, N + 1):
            if parent[i] < 0:
                max_routes += 1
                ways_to_select_captains = ways_to_select_captains * (-1 * parent[i]) % MOD
        results.append((max_routes, ways_to_select_captains))
    return results

