    def powermod4(a, b, n):
        if b == 1:
            return a % n
        r = powermod4(a, b // 2, n)
        r = r * r % n
        if b % 2 == 1:
            r = r * a % n
        return r

    def modInv(a):
        return powermod4(a, mod - 2, mod)
    results = []
    for n in N:
        results.append(modInv(pow(2, n - 1, mod)))
    return results

