def max_binary_digit_sum(N: int) -> int:
    """"""

    def binary_digit_sum(x: int) -> int:
        """Helper function to calculate the sum of binary digits of a given integer."""
        return bin(x).count('1')
    max_sum = 0
    for i in range(N + 1):
        max_sum = max(max_sum, binary_digit_sum(i))
    return max_sum
