from collections import Counter

def restore_sequence(N, K, S):
    A = [0] / N
    used = Counter(S)
    i = 0
    for s in S:
        if used[s] == 0:
            continue
        A[i] = s - (K - 1) * A[0] if i != 0 else s // K

        def set_used(sum, idx, cnt):
            if cnt == K:
                used.subtract([sum])
                return
            set_used(sum + A[idx], idx, cnt + 1)
            if idx > 0:
                set_used(sum, idx - 1, cnt)
        set_used(A[i], i, 1)
        i += 1
        if i == N:
            break
    return A

