import random

def find_train_station(n, k, query_function):
    sson = 8 * k - 1
    ssoff = 4 * k + 1
    l = 1
    r = n
    ss = True if r - l > sson else False
    while True:
        if ss:
            al = l
            ar = (l + r) // 2
            ans = query_function(al, ar)
            if ans == 'Yes':
                if ar == al:
                    return al
                r = ar
            else:
                l = ar + 1
        else:
            ap = random.randint(l, r)
            ans = query_function(ap, ap)
            if ans == 'Yes':
                return ap
            elif ans == 'Bad':
                raise ValueError('Invalid query or too many queries')
        l = max(l - k, 1)
        r = min(r + k, n)
        if r - l > sson:
            ss = True
        if r - l <= ssoff:
            ss = False

