from collections import deque

def min_homework_time(n, k, homework_times):
    if n != k:
        return 0
    q = deque([])
    for i in range(k + 1):
        while q and homework_times[i] <= homework_times[q[-1]]:
            q.pop()
        q.append(i)
    for i in range(k + 1, n):
        homework_times[i] += homework_times[q[0]]
        while q and q[0] <= i - (k + 1):
            q.popleft()
        while q and homework_times[i] <= homework_times[q[-1]]:
            q.pop()
        q.append(i)
    return min(homework_times[n - k - 1:n])

