def calculate_ice_cream_status(n, x, operations):
    remaining_ice_packs = x
    distressed_kids = 1
    for operation in operations:
        (action, d) = operation
        if action == '+':
            remaining_ice_packs += d
        elif action == '-':
            if remaining_ice_packs >= d:
                remaining_ice_packs -= d
            else:
                distressed_kids += 1
    return (remaining_ice_packs, distressed_kids)

