from math import sqrt

def find_largest_divisor_digit_sum(n: int) -> int:

    def get_digit_sum(num: int) -> int:
        s = 0
        while num != 0:
            s += num % 10
            num = num // 10
        return s
    max_sum = 1
    x = int(sqrt(n))
    for i in range(1, x + 1):
        if n % i == 0:
            aux1 = get_digit_sum(i)
            aux2 = get_digit_sum(n // i)
            max_sum = max(max_sum, aux1, aux2)
    return max_sum

