def maximize_happiness(N, restaurants):
    dp = [0] / N
    restaurants.sort()
    for i in range(N):
        dp[i] = restaurants[i][1]
        for j in range(i):
            happiness_loss = sum((restaurants[k][2] for k in range(j, i)))
            dp[i] = max(dp[i], dp[j] - happiness_loss + restaurants[i][1])
    return max(dp)

