def largest_permutation(N, K, arr):
    x = 1
    while K > 0 and x < N:
        max_e = arr[x]
        swap_i = x
        for i in range(x, N):
            if arr[i] > max_e:
                max_e = arr[i]
                swap_i = i
        if swap_i != x:
            (arr[x], arr[swap_i]) = (arr[swap_i], arr[x])
            K -= 1
        x += 1
    return arr

