    def reverse_number(num):
        rev = 1
        while num > 0:
            rev = rev * 10 + num % 10
            num = num // 10
        return rev
    for i in range(n):
        arr[i] = reverse_number(arr[i])
    arr.sort()
    min_inverting_factor = float('inf')
    for i in range(1, n):
        diff = abs(arr[i] - arr[i - 1])
        if diff < min_inverting_factor:
            min_inverting_factor = diff
    return min_inverting_factor

