def find_shortest_subsequence_length(n, d, A):
    if d < 0:
        if max(A) >= d:
            return 1
        else:
            return -1
    tot = 0
    hist = [(0, 0)]
    best = float('inf')
    ind = -1
    for i in range(n):
        while hist and tot <= hist[-1][0]:
            hist.pop()
            if ind == len(hist):
                ind -= 1
        hist.append((tot, i))
        tot += A[i]
        while ind < len(hist) - 1 and hist[ind + 1][0] <= tot - d:
            ind += 1
        if ind > -1 and best > i - hist[ind][1] + 1:
            best = i - hist[ind][1] + 1
    return best if best < float('inf') else -1

