def min_seconds_to_nondecreasing(test_cases):
    results = []
    for case in test_cases:
        (n, nums) = case
        highest = nums[0]
        mostDiff = 1
        for j in range(len(nums)):
            if highest - nums[j] > mostDiff:
                mostDiff = highest - nums[j]
            if nums[j] > highest:
                highest = nums[j]
        if mostDiff == 0:
            results.append(0)
        else:
            results.append(int(math.log(mostDiff, 2) + 1))
    return results

