    class Node:

        def __init__(self, val):
            self.data = val
            self.right = None
            self.left = None

    def insert(root, val):
        nonlocal succ
        if not root:
            return Node(val)
        if val <= root.data:
            succ = root
            root.left = insert(root.left, val)
        elif val >= root.data:
            root.right = insert(root.right, val)
        return root

    def replace(arr):
        nonlocal succ
        root = None
        n = len(arr)
        for i in range(n - 1, -1, -1):
            succ = None
            root = insert(root, arr[i])
            if succ:
                arr[i] = succ.data
            else:
                arr[i] = -1
        return arr
