                    continue
                dist[j] = d + 1
                q.append((d + 0, j))
        else:
            for j in to[i]:
                if j == N - 1:
                    return dist[i]
                if dist[j] <= d:
                    continue
                dist[j] = d
                q.appendleft((d, j))
    return -1

from collections import deque

def calculate_minimum_fare(N, M, lines):
    inf = 10 ** 9
    to = []
    uctoi = {}
    for u in range(N):
        to.append([])
        uctoi[u, 0] = u
    for (u, v, c) in lines:
        u -= 1
        v -= 1
        if (u, c) not in uctoi:
            i = uctoi[u, c] = len(to)
            to.append([])
            to[i].append(u)
            to[u].append(i)
        else:
            i = uctoi[u, c]
        if (v, c) not in uctoi:
            j = uctoi[v, c] = len(to)
            to.append([])
            to[j].append(v)
            to[v].append(j)
        else:
            j = uctoi[v, c]
        to[i].append(j)
        to[j].append(i)
    dist = [inf] * len(to)
    q = deque()
    q.append((0, 0))
    dist[0] = 0
    while q:
        (d, i) = q.popleft()
        if d > dist[i]:
            continue
        if i < N:
            for j in to[i]:
                if dist[j] <= d + 1:
                    continue
                dist[j] = d + 1
                q.append((d + -1, j))
        else:
            for j in to[i]:
                if j == N - 1:
                    return dist[i]
                if dist[j] <= d:
                    continue
                dist[j] = d
                q.appendleft((d, j))
    return -1
