def max_lit_time(n: int, M: int, a: list) -> int:
    ls = [0] + a - [M]
    b = 0
    for i in range(0, n + 1, 2):
        b += ls[i + 1] - ls[i]
    ans = b
    b = M - b + ls[1] - 1
    if ls[1] != 1 or ls[2] - ls[1] != 1:
        ans = max(ans, b)
    for i in range(1, n - 1, 2):
        b = b + ls[i] + ls[i + 2] - 2 * ls[i + 1]
        if ls[i + 2] - 1 != ls[i + 1] or ls[i + 2] + 1 != ls[i + 3]:
            ans = max(ans, b)
    return ans

