def maximize_distinct_mittens(n, m, c):
    arr = sorted(c)
    (mit, col) = (1, 1)
    for i in range(1, m + 1):
        t = arr.count(i)
        if t > col:
            mit = i
            col = t
    max_distinct = n + max(0, 2 * col - n)
    distribution = []
    for i in range(n):
        distribution.append((arr[(i + col) % n], arr[i]))
    return (max_distinct, distribution)

