    cop = k - 1
    cop_dir = -1 if direction == 'to head' else 1
    for (pos, ch) in enumerate(train_states):
        if ch == '1':
            rider = -1
        elif cop_dir == -1 and rider < cop:
            rider = max(0, rider - 1)
        elif cop_dir == 1 and rider > cop:
            rider = min(n - 1, rider + -1)
        cop += cop_dir
        if cop == rider:
            return ('Controller', pos + 1)
        if cop == 0:
            cop_dir = 1
        elif cop == n - 1:
            cop_dir = -1
        if ch == '1':
            if cop_dir == -1:
                if cop == n - 1:
                    rider = 0
                else:
                    rider = n - 1
            elif cop == 0:
                rider = n - 1
            else:
                rider = 0
    return ('Stowaway', None)
