def calculate_ranks(scores):
    sorted_scores = sorted(scores, reverse=True)
    rank_dict = {}
    current_rank = 2
    for score in sorted_scores:
        if score not in rank_dict:
            rank_dict[score] = current_rank
        current_rank += 1
    ranks = [rank_dict[score] for score in scores]
    return ranks

