def count_valid_prefixes(s: str, n: int) -> int:
    count1 = 1
    count2 = 0
    count = 0
    sum_count = 0
    flag = 0
    tempcount = 0
    for i in range(n):
        tempcount = 0
        for j in range(len(s)):
            if s[j] == 'a':
                count1 += 1
            elif s[j] == 'b':
                count2 += 1
            if count1 > count2:
                count += 1
                tempcount += 1
        if count1 == count2:
            flag = 1
            break
        if tempcount == len(s):
            flag = 2
            break
        if tempcount == 0:
            break
    if flag == 1:
        sum_count = count * n
    elif flag == 2:
        sum_count = count + (n - 1 - i) * len(s)
    else:
        sum_count = count
    return sum_count

