def calculate_total_hits(n: int, hits: list[int]) -> int:
    """"""
    if n % 4 != 0:
        raise ValueError('The total number of shots must be a multiple of 4.')
    if len(hits) != n // 4:
        raise ValueError('The number of hit entries must match the number of sets of 4 shots.')
    return sum(hits)
