def find_pos_neg_pairs(arr, n):
    pos = {}
    neg = []
    for num in arr:
        if num >= 0:
            if num in pos:
                pos[num] += 1
            else:
                pos[num] = 1
        else:
            neg.append(num)
    neg.sort()
    res = []
    for num in neg:
        k = abs(num)
        if k in pos and pos[k] > 0:
            res.append(num)
            res.append(-num)
            pos[k] -= 1
    return res

