def calculate_mean_max_scores(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, questions) = case
        scores = {i: 10 for i in range(1, N + 1)}
        for question in questions:
            (i, j, k) = question
            for roll in range(i, j + 1):
                scores[roll] *= k
        total_score = sum(scores.values())
        mean_max_score = total_score / N
        results.append(mean_max_score)
    return results

