from heapq import heappop, heappush, heapify

def find_kth_lexicographical_substring(s: str, k: int) -> str:
    n = len(s)
    if k >= n * (n + 1) / 2:
        return 'No such line.'
    ss = [(s[i], i) for i in range(n)]
    heapify(ss)
    while k > 0:
        k -= 1
        t = heappop(ss)
        if k == 0:
            return t[0]
        elif t[1] < n - 1:
            heappush(ss, (t[0] + s[t[1] + 1], t[1] + 1))
    return 'No such line.'

