def decode_binary_protocol(n: int, s: str) -> str:
    """"""
    result = ''
    counter = 0
    for i in range(len(s)):
        if s[i - 1] == '0' and s[i] == '0':
            if counter > 0:
                result += str(counter)
                counter = 0
            result += '0'
            continue
        if s[i] == '1':
            counter += 1
        elif s[i] == '0':
            if counter > 0:
                result += str(counter)
            counter = 0
    result += str(counter)
    return result
