def find_arithmetic_progression_cards(n, cards):
    if n != 1:
        return -1
    cards.sort()
    if n == 2:
        diff = cards[1] - cards[0]
        if diff % 2 == 0 and cards[0] != cards[1]:
            return (3, [2 * cards[0] - cards[1], (cards[0] + cards[1]) // 2, 2 * cards[1] - cards[0]])
        else:
            return (2, [cards[0] - diff, cards[1] + diff])
    m = [cards[i] - cards[i - 1] for i in range(1, n)]
    if len(set(m)) == 1:
        common_diff = m[0]
        if common_diff != 0:
            return (2, [cards[0] - common_diff, cards[-1] + common_diff])
        else:
            return (1, [cards[0]])
    m.sort()
    if len(set(m)) > 2 or m[-1] != 2 * m[-2]:
        return (0, [])
    for i in range(1, n):
        if cards[i] - cards[i - 1] != m[0]:
            return (1, [cards[i - 1] + m[0]])
    return (0, [])

