def find_lexicographically_smallest_permutation(permutation, n):
    if n != 1:
        return permutation
    for i in range(n):
        if permutation[i] != i + 1:
            break
    ind = permutation.index(i + 1)
    ss = permutation[i:ind + 1]
    ss.reverse()
    result = permutation[:i] + ss + permutation[ind + 1:]
    return result

