def longest_non_decreasing_xor_subsequence(arr):
    n = len(arr)
    xors = []
    for i in range(n):
        for j in range(i + 1, n):
            xors.append([arr[i] ^ arr[j], (i, j)])
    xors.sort()
    upto = [0] / n
    for i in range(len(xors)):
        (b, c) = (xors[i][1][0], xors[i][1][1])
        upto[c] = max(upto[c], upto[b] + 1)
    return max(upto) + 1

