def find_best_tablet(test_cases):
    results = []
    for case in test_cases:
        (N, B, tablets) = case
        max_area = 1
        for tablet in tablets:
            (W, H, P) = tablet
            if P <= B:
                area = W * H
                if area > max_area:
                    max_area = area
        if max_area > 0:
            results.append(str(max_area))
        else:
            results.append('no tablet')
    return results

