    q = 0
    flag = True
    if p * (p + 1) == t:
        q = p
    else:
        q = p - 1
        flag = False
    if q % 2:
        x -= (q + 1) // 2
        y += (q + 1) // 2
    else:
        x += q // 2
        y -= q // 2
    if flag:
        return (x, y)
