        l = q * (q + 1)
        t_new = p * (p + 1)
        diff = t_new - l
        if x < 0:
            if t - l >= diff // 2:
                y *= -1
                l += diff // 2
                x += t - l
            else:
                y -= t - l
        elif t - l >= diff // 2:
            y *= -1
            y += 1
            l += diff // 2
            x -= t - l
        else:
            y += t - l
        return (x, y)

