def minimum_magic_points(H, N, spells):
    d = [float('inf')] / (H + 1)
    d[0] = 0
    for i in range(1, H + 1):
        for (A, B) in spells:
            if i - A >= 0:
                d[i] = min(d[i], d[i - A] + B)
            else:
                d[i] = min(d[i], B)
    return d[H]

