def calculate_joe_calm_down_time(n, m, house):
    current_floor = list(house[0])
    (x, t, direction) = (1, 0, 1)
    for i in range(1, n):
        floor = list(house[i])
        (l, r) = (x, x)
        wall = 0
        while True:
            t += 1
            if floor[x] == '.':
                break
            if x + direction == m or x + direction < 0 or current_floor[x + direction] == '#':
                wall += 1
                direction = -direction
                if wall == 2:
                    return 'Never'
            elif current_floor[x + direction] == '+':
                wall = 0
                current_floor[x + direction] = '.'
                direction = -direction
            elif l <= x + direction <= r:
                if direction == 1:
                    t += r - x - 1
                    x = r
                else:
                    t += x - l - 1
                    x = l
            else:
                x += direction
                r = max(r, x)
                l = min(l, x)
        current_floor = floor
    return t

