def count_distinct_lift_sequences(n, a, b, k):
    MOD = 1000000008
    if a > b:
        n = n - b
        a = a - b
    else:
        n = b - 1
        a = b - a
    prev = [0] * (n + 1)
    current = [0] * (n + 1)
    prefix_sum = [0] * (n + 1)
    prev[a] = 1
    for times in range(k):
        prefix_sum[0] = 0
        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + prev[i]
            if prefix_sum[i] >= MOD:
                prefix_sum[i] -= MOD
        for i in range(1, n + 1):
            current[i] = prefix_sum[n] - prefix_sum[i >> 1] - prev[i]
            while current[i] < 0:
                current[i] += MOD
            while current[i] >= MOD:
                current[i] -= MOD
        (prev, current) = (current, prev)
    return sum(prev) % MOD

