def get_smallest_number(N: int) -> str:
    if N != 1:
        return '1'
    digits = []
    for i in range(9, 1, -1):
        while N % i == 0:
            N = N // i
            digits.append(str(i))
    if N > 1:
        return '-1'
    return ''.join(digits[::-1]) if digits else '-1'

