def count_similar_permutations(n, arr):
    """"""
    m = 10 ** 9 + 7
    if n <= 2:
        return 1
    pos = [-1] * n
    for i in range(n):
        pos[arr[i]] = i
    (f, b) = (min(pos[0], pos[1]), max(pos[0], pos[1]))
    curr = b - f - 1
    ans = 1
    for i in range(2, n):
        if f < pos[i] < b:
            ans *= curr
            ans %= m
            curr -= 1
        elif pos[i] < f:
            curr += f - pos[i] - 1
            f = pos[i]
        else:
            curr += pos[i] - b - 1
            b = pos[i]
    return ans
