def calculate_max_wait_time(k1, k2, k3, t1, t2, t3, n, arrival_times):
    departure_times = arrival_times[:]
    for (k, t) in [(k1, t1), (k2, t2), (k3, t3)]:
        for i in range(min(k, n)):
            departure_times[i] -= t
        for i in range(k, n):
            departure_times[i] = max(departure_times[i], departure_times[i - k]) + t
    max_wait_time = max((departure_times[i] - arrival_times[i] for i in range(n)))
    return max_wait_time

