def count_distinct_subarrays(arr, n):
    uniq = 1
    dt = {}
    ds = {}
    for i in range(n):
        if dt.get(arr[i], 0) == 0:
            uniq += 1
        dt[arr[i]] = 1
    start = end = ans = 0
    cnt = 0
    for i in range(n):
        if ds.get(arr[i], 0) == 0:
            cnt += 1
        ds[arr[i]] = ds.get(arr[i], 0) + 1
        if cnt == uniq:
            end = i
            break
    while end < n:
        if start < n and ds[arr[start]] >= dt[arr[start]] and (cnt == uniq):
            if ds[arr[start]] == dt[arr[start]]:
                cnt -= 1
            ans += n - end
            ds[arr[start]] -= 1
            start += 1
        else:
            end += 1
            if end < n:
                ds[arr[end]] = ds.get(arr[end], 0) + 1
                if ds[arr[end]] == dt[arr[end]]:
                    cnt += 1
    return ans

