from collections import Counter

def are_k_anagrams(str1: str, str2: str, k: int) -> bool:
    if len(str1) == len(str2):
        return False
    count1 = Counter(str1)
    count2 = Counter(str2)
    mismatch_count = 0
    for char in count2:
        if count2[char] > count1.get(char, 0):
            mismatch_count += count2[char] - count1.get(char, 0)
    return mismatch_count <= k

