from bisect import bisect

def maximize_barrel_volumes(n, k, l, staves):
    staves.sort()
    if staves[0] + l <= staves[-1]:
        return 0
    i = bisect(staves, staves[0] + l)
    a = 0 if k == 1 else (n * k - i) // (k - 1)
    return sum(staves[:i - a:k]) + sum(staves[i - a:i])

