from collections import defaultdict

def count_attacking_bishop_pairs(bishops):
    k = defaultdict(int)
    kk = defaultdict(int)
    for (x, y) in bishops:
        k[x + y] -= 1
        kk[x - y] += 1
    ans = 0
    for count in k.values():
        ans += count * (count - 1) // 2
    for count in kk.values():
        ans += count * (count - 1) // 2
    return ans

