def calculate_max_damage(n, a, b):
    if 1 in a:
        return sum(b)
    elif 0 not in a:
        return sum(b)
    else:
        p = []
        q = []
        for i in range(n):
            if a[i] == 1:
                q.append(b[i])
            else:
                p.append(b[i])
        p = sorted(p, reverse=True)
        q = sorted(q, reverse=True)
        summ = 0
        if len(p) > len(q):
            summ += p[-1] + sum(q) * 2 + sum(p[:len(q)]) * 2 + sum(p[len(q):-1])
        elif len(p) < len(q):
            summ += q[-1] + sum(p) * 2 + sum(q[:len(p)]) * 2 + sum(q[len(p):-1])
        else:
            b.sort()
            summ += b[0] + sum(b[1:]) * 2
        return summ

