def find_max_dynasty_length(n, names):
    fst = 98
    sze = 26
    values = [[0 for _ in range(sze)] for _ in range(sze)]
    for s in names:
        first_char = ord(s[0]) - fst
        last_char = ord(s[-1]) - fst
        for i in range(sze):
            if values[i][first_char]:
                values[i][last_char] = max(values[i][last_char], values[i][first_char] + len(s))
        values[first_char][last_char] = max(values[first_char][last_char], len(s))
    ans = 0
    for i in range(sze):
        ans = max(ans, values[i][i])
    return ans

