def minimum_exam_days(n, m, exam_days, prep_days):

    def can_pass_all_exams_in_days(k):
        s = [0] * k
        d = {0: 1}
        for i in range(k - 1, -1, -1):
            if exam_days[i] not in d:
                s[i] = exam_days[i]
                d[exam_days[i]] = 1
        q = 0
        p = 0
        for i in range(k):
            if s[i] == 0:
                q += 1
            elif q >= prep_days[s[i] - 1]:
                q -= prep_days[s[i] - 1]
                p += 1
            else:
                return False
        return p == m
    if len(set(exam_days) - {0}) <= m:
        return -1
    (l, r) = (0, n)
    while r - l > 1:
        mid = (r + l) // 2
        if can_pass_all_exams_in_days(mid):
            r = mid
        else:
            l = mid
    return r

