def qualify_players(N, M, X, throws):
    ans = []
    lvl2 = []
    for i in range(N):
        if throws[i] >= M:
            ans.append(i + 1)
        else:
            lvl2.append((throws[i], i + 1))
    if len(ans) > X:
        return (len(ans), sorted(ans))
    else:
        lvl2.sort(reverse=True, key=lambda x: x[0])
        l = [lvl2[i][1] for i in range(X - len(ans))]
        ans += l
        ans.sort()
        return (X, ans)

