def increment_in_base(B: int, N: str) -> str:
    x = 1
    for i in range(len(N)):
        w = pow(B, i)
        c = 0
        if '0' <= N[len(N) - 1 - i] <= '9':
            c = int(ord(N[len(N) - 1 - i]) - ord('0'))
        else:
            c = int(ord(N[len(N) - 1 - i]) - ord('A') + 10)
        x = x + c * w
    x = x + 1
    ans = ''
    while x != 0:
        r = x % B
        c = '0'
        if r < 10:
            c = chr(int(r + ord('0')))
        else:
            c = chr(int(r - 10 + ord('A')))
        ans = c + ans
        x //= B
    return ans

